pragma solidity ^0.8.0;

import "../interfaces/IERC20.sol";
import "../utils/Context.sol";

contract ERC20 is IERC20, Context {
    string private _name;
    string private _symbol;

    uint256 private _totalSupply;
    
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => uint256) private _balances;
    
    constructor(string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
    }
     
    function name() external view returns(string memory) {
        return _name;
    }
    
    function symbol() external view returns(string memory) {
        return _symbol;
    }
    
    function totalSupply() public view override returns(uint256) {
        return _totalSupply;
    }
     
     
    function balanceOf(address account) external view override returns (uint256) {
         return _balances[account];
    }

   
    function transfer(address recipient, uint256 amount) external override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }
 
    function allowance(address owner, address spender) external view override returns (uint256) {
        return _allowances[owner][spender];   
    }


    function approve(address spender, uint256 amount) external override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external override returns (bool) {
        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(currentAllowance >= amount, "Ruin::Transfer amount exceeds allowance");
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), currentAllowance - amount);
        return true;
    }


    function _transfer(
        address _sender, 
        address _recipient, 
        uint256 _amount
    ) private {
        require(_recipient != address(0), "Ruin::Address of recipient is ilegal");
        require(_sender != address(0), "Ruin::Address of sender is ilegal");
        require(_amount <= _balances[_sender], "Ruin::Transfer amount exceeds account balance");
        
        _balances[_sender] -= _amount;
        _balances[_recipient] += _amount;
        
        emit Transfer(_sender, _recipient, _amount);
    }
    
    function _approve(
        address _approver, 
        address _spender, 
        uint256 _amount
    ) private {
        require(_approver != address(0), "Ruin::Address of approver is ilegal");
        require(_spender != address(0), "Ruin::Address of spender is ilegal");
        
        _allowances[_approver][_spender] = _amount;
        
        emit Approval(_approver, _spender, _amount);
    }
    
    function _mint(address _receiver, uint256 _amount) internal {
        require(_receiver != address(0), "Ruin::Address of receiver is ilegal");
        
        _totalSupply += _amount;
        _balances[_receiver] += _amount;
        
        emit Transfer(address(0), _receiver, _amount);
    }
    
    function _burn(address _account, uint256 _amount) internal {
        require(_account != address(0), "Ruin::Address is ilegal"); 
        require(_balances[_account] >= _amount, "Ruin::Burning amount exceeds account balance");
        
        _totalSupply -= _amount;
        _balances[_account] -= _amount;
        
        emit Transfer(_account, address(0), _amount);
    }

}","keccak256":"0x87869ca8590cf37daf3941a20a3a56c06a1f2d86dcb408a23014990fdf615f0c","license":"MIT"},"contracts/tokens/Ruin.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "../extensions/ERC20Capped.sol";
import "../access/AccessControl.sol";

contract Ruin is ERC20Capped, AccessControl {
    bytes32 constant public MINTER_ROLE = keccak256(abi.encodePacked("MINTER_ROLE"));
    bytes32 constant public BURNER_ROLE = keccak256(abi.encodePacked("BURNER_ROLE"));
    
    constructor(string memory _name, string memory _symbol, uint256 _cap) ERC20(_name, _symbol) ERC20Capped(_cap) {
        _setupRole(_msgSender(), ADMIN_ROLE);
    }
    
    function burn(address _account, uint256 _amount) public onlyRole(BURNER_ROLE) {
        _burn(_account, _amount);
    }
}","keccak256":"0x1258785cde65f2f1ec4ec23694191e20fa12d3861aed8486e80fe88a16ff15a6","license":"MIT"},"contracts/utils/Context.sol":{"content":"// SPDX-License-Identifier: MIT
